import { CART_ADD_ITEM } from "../constants/cartConstant";
import { Axios } from "axios"

export const addToCard = (productId, qty) => async (dispatch, getState) => {
    dispatch({ type: CART_ADD_ITEM, payload: productId })

    const { data } = await Axios.get(`/app/products/${productId}`);
    console.log(data);
    dispatch({
        type: CART_ADD_ITEM,
        payload: {
            name: data.name,
            image: data.image,
            price: data.price,
            countInStock: data.countInStock,
            product: data._id,
            qty,
        }
    })

}

