import React from 'react'
import { useParams } from 'react-router-dom';
import data from './data';
import { FaStar } from "react-icons/fa";
import { Link } from 'react-router-dom'
import Rating from './Rating';


function Product() {
    const { id } = useParams()
    const product = data.products.find((x) => x.id === id)
    // const rating = data.products.rating;


    return <>
        <div class="row d-flex mx-auto">
            <div class="col-12 col-md-5"><img src={product?.Image} className="product-img" alt="product-img" /></div>
            <div class="col-12 col-md-5 mx-auto">
                <div className="card-body">
                    <div className="clearfix mt-3">
                        <h4 className="card-title test-start">
                            {product.description}
                        </h4>
                        <span className=" ms-1 mt-5">
                            <span className='text-dark fs-4 ms-5'>â‚¹</span>&nbsp;<span className='fs-2'>{product?.price}</span></span>
                        <div className='ms-5'>
                            <Rating rating={product.rating} numReviews={product.numReviews} />
                        </div>
                    </div>
                </div>
            </div>
            <div class="col-12 col-md-2 add-to-card">
                add to cart
            </div>
        </div>

        
        {/* <div className="col-md-10 offset-md-1 px-md-2 mt-5" key={product.id}>
            <div className="card-product h-100 shadow-sm">


            </div>
        </div> */}
    </>
}

export default Product
