import React from 'react'
import { useParams } from 'react-router-dom';
import data from './data';
import { Link } from 'react-router-dom'
import Rating from './Rating';
import { useSelector } from 'react-redux';


function Product() {
    const { id } = useParams()
    // const product = data.products.find((x) => x.id === id)

    const productDetails = useSelector((state) => state.productList);
    const { loading, error, product } = productDetails;

    useEffect(() => {
        dispatch(productDetails())
      }, [dispatch])
    
    return <>
        <div className="row d-flex mx-auto">
            <div className="col-12 col-md-5"><img src={product?.Image} className="product-img" alt="product-img" /></div>
            <div className="col-12 col-md-4 px-lg-5">
                <div className="card-body">
                    <div className="clearfix mt-3 ">
                        <h4 className="card-title test-start ms-lg-5">
                            {product.description}
                        </h4>
                        <span className=" ms-lg-1 mt-5">
                            <span className='text-dark fs-4 ms-lg-5'>â‚¹</span>&nbsp;<span className='fs-2'>{product?.price}</span></span>
                        <div className='ms-lg-5'>
                            <Rating rating={product.rating} numReviews={product.numReviews} />
                        </div>
                    </div>
                </div>
            </div>
            <div className="col-12 col-lg-3 add-to-card mx-auto">
                <div className="d-flex justify-content-between">
                    <div className="text-dark fs-4"><span>price</span></div>
                    <div className="fs-5"><span>â‚¹{product.price}</span></div>
                </div>
                <div className="d-flex justify-content-between">
                    <div className="name"><span className='text-dark fs-4'>statues</span>&nbsp;</div>
                    <div className="name"><span className='fs-5 text-success'>{
                        product.countInStock > 0 ? <span>In Stock</span> : <span>Out Stock</span>
                    }</span></div>
                </div>
                    <div>
                        <Link className="btn cart-btn form-control">add cart</Link>
                    </div>
            </div>
        </div>
    </>
}

export default Product
