import {
    PRODUCT_LIST_FAIL,
    PRODUCT_LIST_REQUEST,
    PRODUCT_LIST_SUCCESS,
    PRODUCT_DETAILS_FAIL,
    PRODUCT_DETAILS_REQUEST,
    PRODUCT_DETAILS_SUCCESS
}
    from "../constants/productConstants"
import Axios from 'axios';

export const listProducts = () => async (dispatch) => {
    dispatch({ type: PRODUCT_LIST_REQUEST })
    try {
        const { data } = await Axios.get('/app/products ');
        dispatch({ type: PRODUCT_LIST_SUCCESS, payload: data })
    } catch (error) {
        dispatch({ type: PRODUCT_LIST_FAIL, payload: error.massage })
    }
}


export const singleProduct = (productId) => async (dispatch) => {
    console.log(productId);
    dispatch({  type: PRODUCT_DETAILS_REQUEST, payload: productId })
    try {
        const { data } = await Axios.get(`/app/products/${productId}`);
        
        dispatch({ type: PRODUCT_DETAILS_SUCCESS, payload: data })
    } catch (error) {
        dispatch({
            type: PRODUCT_DETAILS_FAIL, payload: error.massage
        })
    }
}
