import { CART_ADD_ITEM } from "../constants/cartConstant";
import { Axios } from "axios"

export const addToCard = (productId,qty) => async (dispatch) => {
    dispatch({ type: PRODUCT_DETAILS_REQUEST, payload: productId })
    try {
        const { data } = await Axios.get(`/app/products/${productId}`);

        dispatch({
            type: CART_ADD_ITEM,
            payload: {
              name: data.name,
              image: data.image,
              price: data.price,
              countInStock: data.countInStock,
              product: data._id,
              qty,
            }
        })
    } catch (error) {
        dispatch({
            type: PRODUCT_DETAILS_FAIL, payload: error.massage
        })
    }
}

