import React, { useEffect, useState } from 'react'
import Productlist from './Productlist';
import axios from 'axios';
import Loadingmsg from './Loadingmsg';
import Errormsg from './Errormsg';

function Home() {
  // const [product, setProduct] = useState([])
  // const [loading, setLoading] = useState(false)
  // const [error, setError] = useState(false)

  const productList = useSelectore((state)=>state.productList);
  const {product,loading,error} = productList;
  const fetchData = async () => {
    try {
      setLoading(true)
      const { data } = await axios.get('/app/products ');
      setProduct(data);
      setLoading(false)
    } catch (err) {
      setError(err.massage)
      setLoading(true)
    }

  }
  useEffect(() => {
    fetchData()
  }, [])

  return <>
    <div className="container-fluid bg-trasparent my-4 p-3" style={{ position: 'relative' }}>
      <div className="row row-cols-1 row-cols-xs-2 row-cols-sm-2 row-cols-lg-4 g-3">
        {
          loading?(
          <Loadingmsg></Loadingmsg>
          ): error?(
            console.log(error)
          ):(
            product.map((products, id) => {
              return <Productlist key={products.id} products={products}></Productlist>
            })
          )
        }
        
      </div>
    </div>
  </>
}

export default Home
